(function(){new Vue({el:"#app",data:function(){return{date:new Date}},methods:{},computed:{},mounted:function(){}})})(),function(){Vue.component("widget-pie-timer",{template:`
            <svg :width="size" :height="size" :viewbox="'0 0 ' + size + ' ' + size" class="pie-timer text-primary cursor-pointer" @click="isPlaying = !isPlaying">
              <path class="pie-timer-border" :transform="'translate(' + size/2 + ', ' + size/2 + ')'" ref="border"/>
              <path class="pie-timer-loader" :transform="'translate(' + size/2 + ', ' + size/2 + ') scale(.84)'" ref="loader"/>
            </svg>
        `,props:{size:{type:Number,"default":15},speed:{type:Number,"default":15}},data:function(){return{isPlaying:!0,"π":Math.PI,"α":0}},methods:{draw:function(){this.α++;this.α%=360;var n=this.α*this.π/180,i=Math.sin(n)*this.size/2,r=Math.cos(n)*-this.size/2,u=this.α>180?1:0,t="M 0 0 v -"+this.size/2+" A "+this.size/2+" "+this.size/2+" 1 "+u+" 1 "+i+" "+r+" z";this.$refs.loader.setAttribute("d",t);this.$refs.border.setAttribute("d",t);this.α%360==0&&this.$emit("done");this.isPlaying&&setTimeout(this.draw,this.speed)}},watch:{isPlaying:function(n){n&&this.draw()}},mounted:function(){this.draw()}})}();